import {
  Avatar,
  ButtonCloseModal,
  ButtonSearch,
  FormGroup,
  Logo,
  Menu,
  MenuItem,
  MenuRightSide,
  MenuRightSideItem,
  NavbarStyled,
  OverlaySearch,
  SeachModal,
  SearchResult,
  SearchResultItem,
  WrapperMenuAndLogo,
} from "./styles";

import { SearchIcon, BellIcon, CloseIcon } from "./iconesSvg";
import { useState } from "react";

type TMovie = {
  cover: string;
  title: string;
  description: string;
  year: string | number;
};

const Navbar = () => {
  const [showModal, setShowModal] = useState<boolean>(false);

  const results: Array<TMovie> = [
    {
      cover: "/images/photo1.jpg",
      title: "The Porsuit of Happiness",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
  ];

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  return (
    <NavbarStyled>
      {showModal && (
        <OverlaySearch initial={{ opacity: 0.7 }} animate={{ opacity: 1 }}>
          <SeachModal
            initial={{ y: -100 }}
            animate={{ y: 0 }}
            length={results.length}
          >
            <ButtonCloseModal onClick={toggleModal}>
              <CloseIcon />
            </ButtonCloseModal>

            <FormGroup>
              <input type="text" placeholder="search for movie" />
            </FormGroup>

            <SearchResult length={results.length}>
              {results.map((item: TMovie, index) => (
                <SearchResultItem key={index}>
                  <div className="cover"></div>
                  <div className="itemInfo">
                    <h1>{item.title}</h1>
                    <p>{item.description}</p>

                    <p>{item.year}</p>
                  </div>
                </SearchResultItem>
              ))}
            </SearchResult>
          </SeachModal>
        </OverlaySearch>
      )}

      <WrapperMenuAndLogo>
        <Logo src="./images/logo.png" alt="Netflix" />

        <Menu>
          <ButtonSearch onClick={toggleModal}>
            <SearchIcon />
          </ButtonSearch>
          <MenuItem first={true}>BROWSE</MenuItem>
          <span></span>
          <MenuItem>KIDS</MenuItem>
        </Menu>
      </WrapperMenuAndLogo>

      <MenuRightSide>
        <MenuRightSideItem>
          <BellIcon />
        </MenuRightSideItem>
        <MenuRightSideItem>
          <Avatar src="./images/avatar.JPG" />
        </MenuRightSideItem>
      </MenuRightSide>
    </NavbarStyled>
  );
};

export default Navbar;
