import {
  Avatar,
  ButtonSearch,
  Logo,
  Menu,
  MenuItem,
  MenuRightSide,
  MenuRightSideItem,
  NavbarStyled,
  OverlaySearch,
  WrapperMenuAndLogo,
} from "./styles";

import { SearchIcon, BellIcon } from "./iconesSvg";
import { useState } from "react";
import ModalSearch from "./modal";

const Navbar = () => {
  const [showModal, setShowModal] = useState<boolean>(false);

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  return (
    <NavbarStyled>
      {showModal && (
        <OverlaySearch initial={{ opacity: 0.7 }} animate={{ opacity: 1 }}>
          <ModalSearch toggleModal={toggleModal} />
        </OverlaySearch>
      )}

      <WrapperMenuAndLogo>
        <Logo src="./images/logo.png" alt="Netflix" />

        <Menu>
          <ButtonSearch onClick={toggleModal}>
            <SearchIcon />
          </ButtonSearch>
          <MenuItem first={true}>BROWSE</MenuItem>
          <MenuItem>KIDS</MenuItem>
        </Menu>
      </WrapperMenuAndLogo>

      <MenuRightSide>
        <MenuRightSideItem>
          <BellIcon />
          <ul className="notification">
            {[1, 2, 3, 4, 5].map(() => (
              <li>
                <div className="cover"></div>
                <p className="not_text">Lorem ipsum dolor sit amet.</p>
              </li>
            ))}
          </ul>
        </MenuRightSideItem>
        <MenuRightSideItem>
          <Avatar src="./images/avatar.JPG" />
        </MenuRightSideItem>
      </MenuRightSide>
    </NavbarStyled>
  );
};

export default Navbar;
