import styled from "styled-components";
import { flexbox, getFont } from "../../constants/functionsStyles";
import Colors from "./../../constants/colors";

export const NavbarStyled = styled.div`
  width: 100%;
  height: 100px;
  background: ${Colors.bg};

  ${flexbox("row", "space-between", "center")};
  box-sizing: border-box;
  padding-left: 3%;
  padding-right: 3%;
`;

export const WrapperMenuAndLogo = styled.div`
  ${flexbox("row", "space-between", "center")};
  width: 45%;
`;

export const ButtonSearch = styled.button`
  background-color: transparent;
  border: none;
  outline: none;
  cursor: pointer;

  svg {
    fill: ${Colors.text};
    width: 1rem;
  }

  &:hover {
    svg {
      opacity: 0.8;
    }
  }
`;

export const Logo = styled.img`
  width: 7rem;
`;

export const Menu = styled.ul`
  list-style: none;
  ${flexbox()};
  width: 100%;

  span {
    width: 3px;

    background-color: ${Colors.text};
    height: 100%;
    border-radius: 10px;
  }
`;

export const MenuItem = styled.li`
  ${getFont()};
  font-size: 11pt;
  color: ${Colors.text};
  transition: 0.5s;
  cursor: pointer;
  height: 100%;
  margin-left: 30px;
  padding-right: ${({ first }: any) => (first ? `30px` : 0)};

  border-right: ${({ first }: any) =>
    first ? `2px solid ${Colors.text}` : "none"};

  &:hover {
    opacity: 0.8;
  }
`;

export const OverlaySearch = styled.div`
  width: 100%;
  height: 100vh;
  position: fixed;
  left: 0;
  top: 0;
  background-color: rgba(0, 0, 0, 0.5);
  ${flexbox("column", "flex-start", "center")};
  z-index: 99999;
`;

export const SeachModal = styled.div`
  position: relative;
  width: 35%;
  height: ${({ length }) => (length > 2 ? "90vh" : "300px")};
  border-radius: 10px;
  background: ${Colors.bg};
  box-shadow: 0 0 10px ${Colors.bg};
  margin-top: 40px;
  ${flexbox("column", "flex-start", "center")};
  padding-bottom: ${({ length }) => (length > 2 ? "20px" : "0")};
`;

export const ButtonCloseModal = styled.button`
  width: 30px;
  height: 30px;
  outline: none;
  border: none;
  background: red;
  position: absolute;
  top: 0;
  right: 0;
  margin: 10px;

  svg {
    width: 25px;
    height: 25px;
    fill: white;
  }
`;

export const Avatar = styled.div`
  width: 30px;
  height: 30px;
  border-radius: 100%;
  background: ${({ src }: any) => `url(${src})`};
  background-size: cover;
`;

export const FormGroup = styled.div`
  width: 90%;
  height: 35px;
  border-radius: 5px;
  background: ${Colors.text};
  overflow: hidden;
  margin-top: 30px;
  transition: 0.5s;

  &:focus-within {
    box-shadow: 0 0 5px darkmagenta;
  }

  input {
    outline: none;
    border: none;
    width: 100%;
    height: 100%;
    font-size: 12pt;
    ${getFont()};
    background-color: transparent;
    box-sizing: border-box;
    padding-left: 15px;
    color: ${Colors.bg};
  }
`;

export const SearchResult = styled.ul`
  flex: 1;
  list-style: none;
  margin-top: 20px;
  overflow: auto;
`;

export const SearchResultItem = styled.li`
  width: 100%;
  height: 60px;
  margin-bottom: 35px;
  display: grid;
  grid-template-columns: 60px 1fr;

  .cover {
    width: 60px;
    height: 60px;
    border-radius: 10px;
    background: ${({ cover }) => `url(${cover})`};
    background-size: cover;
  }

  .itemInfo {
    margin-left: 10px;
    height: 100%;
    ${flexbox("column", "flex-start", "space-between")};
    padding-top: 1px;

    h1 {
      ${getFont("Medium")};
      font-size: 12pt;
      margin-bottom: 3px;
      color: white;
    }

    p {
      ${getFont()};
      margin-bottom: 3px;
      font-size: 11pt;
      color: white;
    }
  }
`;

export const MenuRightSide = styled(Menu)`
  width: auto;
`;

export const MenuRightSideItem = styled.li`
  margin-left: 30px;
  ${flexbox()};

  svg {
    width: 1.2rem;
    fill: ${Colors.text};
  }
`;
