import styled from "styled-components";
import { flexbox, getFont } from "../../constants/functionsStyles";
import Colors from "./../../constants/colors";

export const NavbarStyled = styled.div`
  width: 100%;
  height: 100px;
  background: ${Colors.bg};
  position: absolute;
  top: 0;
  left: 0;
  ${flexbox("row", "space-between", "center")};
  box-sizing: border-box;
  padding-left: 3%;
  padding-right: 3%;
`;

export const WrapperMenuAndLogo = styled.div`
  ${flexbox("row", "space-between", "center")};
  width: 45%;
`;

export const ButtonSearch = styled.button`
  background-color: transparent;
  border: none;
  outline: none;
  cursor: pointer;

  svg {
    fill: ${Colors.text};
    width: 1rem;
  }

  &:hover {
    svg {
      opacity: 0.8;
    }
  }
`;

export const Logo = styled.img`
  width: 7rem;
`;

export const Menu = styled.ul`
  list-style: none;
  ${flexbox()};
  width: 100%;
`;

export const MenuItem = styled.li`
  ${getFont("Bold")};
  width: 150px;
  color: ${Colors.text};
  text-align: center;
  transition: 0.5s;
  cursor: pointer;

  &:first-child {
    border-right: 2px solid red;
  }

  &:hover {
    opacity: 0.8;
  }
`;

export const SeachModal = styled.div``;

export const Avatar = styled.img`
  width: 1.5rem;
`;

export const MenuRightSide = styled.ul``;

export const MenuRightSideItem = styled.li``;
