export const WrapperMenuAndLogo = styled.div`
  ${flexbox("row", "space-between", "center")};
  width: 45%;
`;

export const ButtonSearch = styled.button`
  background-color: transparent;
  border: none;
  outline: none;
  cursor: pointer;

  svg {
    fill: ${Colors.text};
    width: 2.3rem;
    transition: 0.5s;

    @media (max-width: 2500px) {
      width: 2rem;
    }

    @media (max-width: 2000px) {
      width: 1.7rem;
    }

    @media (max-width: 1500px) {
      width: 1rem;
    }
  }

  &:hover {
    svg {
      fill: white;
    }
  }
`;

export const Logo = styled.img`
  width: 20rem;

  @media (max-width: 2500px) {
    width: 18rem;
  }

  @media (max-width: 2000px) {
    width: 13rem;
  }

  @media (max-width: 1500px) {
    width: 7rem;
  }
`;

export const Menu = styled.ul`
  list-style: none;
  ${flexbox()};
  width: 100%;
`;

export const MenuItem = styled.li`
  ${getFont()};
  font-size: 25pt;
  color: ${Colors.text};
  transition: 0.5s;
  cursor: pointer;
  height: 100%;
  margin-left: 70px;
  padding-right: ${({ first }: any) => (first ? `70px` : 0)};

  border-right: ${({ first }: any) =>
    first ? `2px solid ${Colors.text}` : "none"};

  &:hover {
    color: white;
  }

  @media (max-width: 2500px) {
    font-size: 20pt;
  }

  @media (max-width: 2000px) {
    font-size: 15pt;
  }

  @media (max-width: 1500px) {
    font-size: 11pt;
    margin-left: 30px;
    padding-right: ${({ first }: any) => (first ? `30px` : 0)};
  }
`;

export const MenuRightSide = styled(Menu)`
  width: auto;
`;

export const MenuRightSideItem = styled.li`
  margin-left: 30px;
  ${flexbox()};
  cursor: pointer;
  transition: 0.5s;
  position: relative;

  svg {
    width: 1.2rem;
    fill: ${Colors.text};
    transition: 0.5s;
  }

  &:hover {
    svg {
      fill: white;
