export const Notification = styled(motion.ul)`
  width: 250px;
  height: auto;
  border-radius: 5px;
  top: 35px;
  right: -30%;
  position: absolute;
  z-index: 9999;
  background: ${Colors.bg};
  list-style: none;

  li {
    width: 100%;
    background: ${Colors.bg};
    margin-bottom: 5px;
    ${flexbox("row", "flex-start", "center")};
    box-sizing: border-box;
    padding-left: 10px;
    padding-top: 7px;
    padding-bottom: 7px;
    box-shadow: 0 0 3px white;
    border-radius: 5px;

    &:last-child {
      margin-bottom: 0;
    }

    .cover {
      width: 40px;
      height: 40px;
      border-radius: 100%;
      background: red;
    }

    .not_text {
      ${getFont()};
      font-size: 10pt;
      color: black;
      margin-left: 10px;
      color: white;
    }
  }
`;

export const OverlaySearch = styled(motion.div)`
  width: 100%;
  height: 100vh;
  position: fixed;
  left: 0;
  top: 0;
  background-color: rgba(0, 0, 0, 0.5);
  ${flexbox("column", "flex-start", "center")};
  z-index: 99999;
`;

export const SeachModal = styled(motion.div)`
  position: relative;
  width: 35%;
  height: ${({ length }) => (length > 2 ? "85vh" : "300px")};
  border-radius: 10px;
  background: ${Colors.bg};
  box-shadow: 0 0 10px ${Colors.bg};
  margin-top: 40px;
  ${flexbox("column", "flex-start", "center")};
  padding-bottom: ${({ length }) => (length > 2 ? "20px" : "0")};
`;

export const ButtonCloseModal = styled.button`
  width: 30px;
  height: 30px;
  outline: none;
  border: none;
  background: transparent;
  align-self: flex-end;
  margin: 10px;
  cursor: pointer;

  svg {
    width: 25px;
    height: 25px;
    fill: white;
  }
`;

export const Avatar = styled.div`
  width: 30px;
  height: 30px;
  border-radius: 100%;
  background: ${({ src }: any) => `url(${src})`};
  background-size: cover;
`;

export const FormGroup = styled.div`
  width: 90%;
  height: 35px;
  border-radius: 5px;
  background: ${Colors.text};
  overflow: hidden;
  margin-top: 10px;
  transition: 0.5s;

  &:focus-within {
    box-shadow: 0 0 5px darkmagenta;
  }

  input {
    outline: none;
    border: none;
    width: 100%;
    height: 100%;
    font-size: 12pt;
    ${getFont()};
    background-color: transparent;
    box-sizing: border-box;
    padding-left: 15px;
    color: ${Colors.bg};
  }
