export const SearchResult = styled.ul`
  flex: 1;
  list-style: none;
  margin-top: 20px;
  overflow: auto;
  ${flexbox("column")};
  scroll-behavior: smooth;

  ::-webkit-scrollbar {
    width: 6px;
    padding-right: 5px;
  }

  ::-webkit-scrollbar-track {
    background: transparent;
  }

  ::-webkit-scrollbar-thumb {
    background: #888;
    border-radius: 30px;
  }
`;

export const SearchResultItem = styled(motion.li)`
  width: 90%;
  height: 60px;
  margin-bottom: 35px;
  display: grid;
  grid-template-columns: 60px 1fr;
  cursor: pointer;

  .cover {
    width: 60px;
    height: 60px;
    border-radius: 10px;
    background: ${({ cover }) => `url(${cover})`};
    background-size: cover;
  }

  .itemInfo {
    margin-left: 10px;
    height: 100%;
    ${flexbox("column", "flex-start", "space-between")};
    padding-top: 1px;

    h1 {
      ${getFont()};
      font-size: 12pt;
      margin-bottom: 3px;
      color: white;
    }

    p {
      ${getFont()};
      margin-bottom: 3px;
      font-size: 11pt;
      color: white;
    }
  }
