import { useState } from "react";
import { ListMovieStyled, SingleMovie } from "./styles";

type TListMovies = {
  movies: Array<any>;
};

const ListMovie = ({ movies }: TListMovies) => {
  const [scaleCover, setScale] = useState<boolean>(false);

  return (
    <ListMovieStyled>
      {movies.map((item, index) => (
        <SingleMovie
          initial={{ scale: 0.1 }}
          animate={{ scale: scaleCover ? 1.3 : 1 }}
          transition={{ duration: 0.7 }}
          onMouseOver={() => setScale(true)}
          key={index}
          image={item}
        />
      ))}
    </ListMovieStyled>
  );
};

export default ListMovie;
