import {
  Avatar,
  ButtonCloseModal,
  ButtonSearch,
  FormGroup,
  Logo,
  Menu,
  MenuItem,
  MenuRightSide,
  MenuRightSideItem,
  NavbarStyled,
  OverlaySearch,
  SeachModal,
  SearchResult,
  SearchResultItem,
  WrapperMenuAndLogo,
} from "./styles";

import { SearchIcon, BellIcon, CloseIcon } from "./iconesSvg";
import { useState } from "react";

type TMovie = {
  cover: string;
  title: string;
  description: string;
  year: string | number;
};

const Navbar = () => {
  const [showModal, setShowModal] = useState<boolean>(false);

  const results: Array<TMovie> = [
    {
      cover: "/images/photo1.jpg",
      title: "The Porsuit of Happiness",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },

    {
      cover: "/images/photo2.jpg",
      title: "O Banquete",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
    {
      cover: "/images/photo3.jpg",
      title: "The ratatuil",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
    {
      cover: "/images/photo4.jpg",
      title: "The Age of YSL",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
    {
      cover: "/images/photo5.jpg",
      title: "tHE OPPRESSOR",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
    {
      cover: "/images/photo6.jpg",
      title: "The life is hard",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
  ];

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  return (
    <NavbarStyled>
      {showModal && (
        <OverlaySearch>
          <SeachModal
            initial={{ opacity: 0.7 }}
            animate={{ opacity: 1 }}
            length={results.length}
          >
            <ButtonCloseModal onClick={toggleModal}>
              <CloseIcon />
            </ButtonCloseModal>

            <FormGroup>
              <input type="text" placeholder="search for movie" />
            </FormGroup>

            <SearchResult length={results.length}>
              {results.map((item: TMovie, index) => (
                <SearchResultItem key={index} cover={item.cover}>
                  <div className="cover"></div>
                  <div className="itemInfo">
                    <h1>{item.title}</h1>
                    <p>{item.description}</p>

                    <p>{item.year}</p>
                  </div>
                </SearchResultItem>
              ))}
            </SearchResult>
          </SeachModal>
        </OverlaySearch>
      )}

      <WrapperMenuAndLogo>
        <Logo src="./images/logo.png" alt="Netflix" />

        <Menu>
          <ButtonSearch onClick={toggleModal}>
            <SearchIcon />
          </ButtonSearch>
          <MenuItem first={true}>BROWSE</MenuItem>
          <span></span>
          <MenuItem>KIDS</MenuItem>
        </Menu>
      </WrapperMenuAndLogo>

      <MenuRightSide>
        <MenuRightSideItem>
          <BellIcon />
        </MenuRightSideItem>
        <MenuRightSideItem>
          <Avatar src="./images/avatar.JPG" />
        </MenuRightSideItem>
      </MenuRightSide>
    </NavbarStyled>
  );
};

export default Navbar;
