import {
  Avatar,
  ButtonCloseModal,
  ButtonSearch,
  FormGroup,
  Logo,
  Menu,
  MenuItem,
  MenuRightSide,
  MenuRightSideItem,
  NavbarStyled,
  OverlaySearch,
  SeachModal,
  SearchResult,
  SearchResultItem,
  WrapperMenuAndLogo,
} from "./styles";

import { SearchIcon, BellIcon, CloseIcon } from "./iconesSvg";
import { useState } from "react";

type TMovie = {
  cover: string;
  title: string;
  description: string;
  year: string | number;
};

const Navbar = () => {
  const [showModal, setShowModal] = useState<boolean>(false);

  const toggleModal = () => {
    setShowModal(!showModal);
  };

  return (
    <NavbarStyled>
      {showModal && (
        <OverlaySearch initial={{ opacity: 0.7 }} animate={{ opacity: 1 }}>
          <ModalSearch toggleModal={toggleModal} />
        </OverlaySearch>
      )}

      <WrapperMenuAndLogo>
        <Logo src="./images/logo.png" alt="Netflix" />

        <Menu>
          <ButtonSearch onClick={toggleModal}>
            <SearchIcon />
          </ButtonSearch>
          <MenuItem first={true}>BROWSE</MenuItem>
          <span></span>
          <MenuItem>KIDS</MenuItem>
        </Menu>
      </WrapperMenuAndLogo>

      <MenuRightSide>
        <MenuRightSideItem>
          <BellIcon />
        </MenuRightSideItem>
        <MenuRightSideItem>
          <Avatar src="./images/avatar.JPG" />
        </MenuRightSideItem>
      </MenuRightSide>
    </NavbarStyled>
  );
};

export default Navbar;
