import {
  Avatar,
  ButtonSearch,
  FormGroup,
  Logo,
  Menu,
  MenuItem,
  MenuRightSide,
  MenuRightSideItem,
  NavbarStyled,
  OverlaySearch,
  SeachModal,
  SearchResult,
  SearchResultItem,
  WrapperMenuAndLogo,
} from "./styles";

import { SearchIcon, BellIcon } from "./iconesSvg";

type TMovie = {
  cover: string;
  title: string;
  description: string;
  year: string | number;
};

const Navbar = () => {
  const results: Array<TMovie> = [
    {
      cover: "/images/photo1.jpg",
      title: "The Porsuit of Happiness",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },

    {
      cover: "/images/photo2.jpg",
      title: "O Banquete",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
    {
      cover: "/images/photo3.jpg",
      title: "The ratatuil",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
    {
      cover: "/images/photo4.jpg",
      title: "The Age of YSL",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
    {
      cover: "/images/photo5.jpg",
      title: "tHE OPPRESSOR",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
    {
      cover: "/images/photo6.jpg",
      title: "The life is hard",
      description: `Lorem ipsum dolor sit amet consectetur adipisicing elit.
      Dolores placeat accusamus reiciendis.`,
      year: 2020,
    },
  ];

  return (
    <NavbarStyled>
      <OverlaySearch>
        <SeachModal>
          <FormGroup>
            <input type="text" placeholder="search for movie" />
          </FormGroup>

          <SearchResult length={results.length}>
            {results.map((item: TMovie, index) => (
              <SearchResultItem key={index} cover={item.cover}>
                <div className="cover"></div>
                <div className="itemInfo">
                  <h1>{item.title}</h1>
                  <p>{item.description}</p>

                  <p>{item.year}</p>
                </div>
              </SearchResultItem>
            ))}
          </SearchResult>
        </SeachModal>
      </OverlaySearch>

      <WrapperMenuAndLogo>
        <Logo src="./images/logo.png" alt="Netflix" />

        <Menu>
          <ButtonSearch>
            <SearchIcon />
          </ButtonSearch>
          <MenuItem first={true}>BROWSE</MenuItem>
          <span></span>
          <MenuItem>KIDS</MenuItem>
        </Menu>
      </WrapperMenuAndLogo>

      <MenuRightSide>
        <MenuRightSideItem>
          <BellIcon />
        </MenuRightSideItem>
        <MenuRightSideItem>
          <Avatar src="./images/avatar.JPG" />
        </MenuRightSideItem>
      </MenuRightSide>
    </NavbarStyled>
  );
};

export default Navbar;
