import styled from "styled-components";
import { flexbox, getFont } from "../../constants/functionsStyles";
import Colors from "./../../constants/colors";

export const NavbarStyled = styled.div`
  width: 100%;
  height: 100px;
  background: ${Colors.bg};

  ${flexbox("row", "space-between", "center")};
  box-sizing: border-box;
  padding-left: 3%;
  padding-right: 3%;
`;

export const WrapperMenuAndLogo = styled.div`
  ${flexbox("row", "space-between", "center")};
  width: 45%;
`;

export const ButtonSearch = styled.button`
  background-color: transparent;
  border: none;
  outline: none;
  cursor: pointer;

  svg {
    fill: ${Colors.text};
    width: 1rem;
  }

  &:hover {
    svg {
      opacity: 0.8;
    }
  }
`;

export const Logo = styled.img`
  width: 7rem;
`;

export const Menu = styled.ul`
  list-style: none;
  ${flexbox()};
  width: 100%;

  span {
    width: 3px;

    background-color: ${Colors.text};
    height: 100%;
    border-radius: 10px;
  }
`;

export const MenuItem = styled.li`
  ${getFont()};
  font-size: 11pt;
  color: ${Colors.text};
  transition: 0.5s;
  cursor: pointer;
  height: 100%;
  margin-left: 30px;
  padding-right: ${({ first }: any) => (first ? `30px` : 0)};

  border-right: ${({ first }: any) =>
    first ? `2px solid ${Colors.text}` : "none"};

  &:hover {
    opacity: 0.8;
  }
`;

export const OverlaySearch = styled.div`
  width: 100%;
  height: 100vh;
  position: fixed;
  left: 0;
  top: 0;
  background-color: rgba(0, 0, 0, 0.5);
  ${flexbox("column", "flex-start", "center")};
`;

export const SeachModal = styled.div`
  width: 30%;
  height: 300px;
  border-radius: 30px;
  background: ${Colors.bg};
  box-shadow: 0 0 10px gray;
  margin-top: 40px;
`;

export const Avatar = styled.div`
  width: 30px;
  height: 30px;
  border-radius: 100%;
  background: ${({ src }: any) => `url(${src})`};
  background-size: cover;
`;

export const MenuRightSide = styled(Menu)`
  width: auto;
`;

export const MenuRightSideItem = styled.li`
  margin-left: 30px;
  ${flexbox()};

  svg {
    width: 1.2rem;
    fill: ${Colors.text};
  }
`;
