export const getFont = (font: string = "Regular") => `
    @font-face {
        src: url(./../assets/fonts/Roboto-${font}.ttf);
        font-family: Roboto-${font};
    }

    font-family: Roboto-${font};

`;

export const flebox = (
  dir: string = "row",
  jus: string = "center",
  alig: string = "center"
) => `
    display:flex;
    flex-direction: ${dir};
    align-items:${alig};
    justify-content: ${jus};
`;
